	foreach ( $all_pages as $post_name => $dummy_content ) {
	// Retrieving page object given its title. It is stored in wp_posts table.
	// When no object found, then that page does not exist. We will generate it.
	if( null != get_page_by_title( $post_name ) )  echo $post_name.' page exists<br/>';
	if( null == get_page_by_title( $post_name ) )  making_page($post_name, $dummy_content);
	}
	
	// We need to include file post.php to use post_exists() function
	if ( !function_exists( 'post_exists' ) ) { 
    require_once ABSPATH . '/wp-admin/includes/post.php'; 
    }
	// Iterating over our array with posts. 
	// If we have such post title in DB - we echo "Given post exists", otherwise that post is created
	foreach ( $all_posts as $post_name => $dummy_content ) {
	if( 0 != post_exists($post_name) )  echo $post_name . ' post exists<br/>';
	if( 0 == post_exists($post_name) )  making_post($post_name, $dummy_content);
	}

}

// Generating a page and writing a notice in sidebar
function making_page($post_name, $dummy_content) {
	$post_data = array(
     'post_title'    => $post_name,
     'post_name'     => $post_name,
     'post_content'  => $dummy_content,
     'post_status'   => 'publish',
     'post_author'   => 1,
     'post_type'     => 'page'
    );
	$post_id = wp_insert_post( wp_slash($post_data) );	
	echo $post_name.' page was created<br/>';
}



// Generating a post and writing a notice in sidebar
function making_post($post_name, $dummy_content) {
	$post_data = array(
     'post_title'    => $post_name,
     'post_name'     => $post_name,
     'post_content'  => $dummy_content,
     'post_status'   => 'publish',
     'post_author'   => 1,
     'post_type'     => 'post'
    );
	$post_id = wp_insert_post( wp_slash($post_data) );	
	echo $post_name.' post was created<br/>';
}


